let item1={id:101,name:'Apple Iphone',img:"data:image/jpeg;base64,",price:20000,qty:1};
let item2={id:102,name:'Bata',img:"data:image/jpeg;base64,",price:2000,qty:1};
let item3={id:103,name:'Hp Laptops',img:"data:image/jpeg;base64,",price:65000,qty:1};
let item4={id:104,name:'Sony TV',img:"data:image/jpeg;base64,",price:76000,qty:1};

let productsArr=[item1,item2,item3,item4];

function displayProducts(items){
    let product='';
    for(let item of items){

        let {img,name,price,qty,id}=item;  //destructuring the object and getting the keys directly

        product+=`<div class="col-3">
                        <div class="card">
                            <div class="card-header text-center">
                                <img src="${img}" alt="" class="img-fluid" >
                            </div>
                            <div class="card-body text-center">
                                <h3>${name}</h3>
                                <p>Lorem ipsum dolor sit amet.</p>
                                <h5>${price} &#8377</h5>
                                <h6>
                                    <i class="bi bi-dash-circle" onclick=decQty(${id})></i>
                                    <span>${qty}</span>
                                    <i class="bi bi-plus-circle" onclick="incQty(${id})"></i>
                                </h6>
                                <button class='btn btn-warning'>Add To Cart</button>
                            </div>
                        </div>
                    </div>`;
    }
    document.getElementById('display').innerHTML=product; 
}
displayProducts(productsArr)

function incQty(id){
    let incArr=productsArr.map((item)=>{
        if(item.id==id){
            let obj={...item,qty:++item.qty};   //we are using spread operator to get the objects and spread it and we are incrementing or decrementing only that particular object
            return obj;   //after updating we are returning to that array
        }
        else return item;  //if the id is not matching means we dont have to change it
    });
    displayProducts(incArr)
}

function decQty(id){
    let decArr=productsArr.map((item)=>{
        if(item.id==id && item.qty > 1){
            let obj={...item,qty:--item.qty};   //we are using spread operator to get the objects and spread it and we are incrementing or decrementing only that particular object
            return obj;   //after updating we are returning to that array
        }
        else return item;  //if the id is not matching means we dont have to change it
    });
    displayProducts(decArr)
}